parameters.inputSchema:
{
  "name": "resume_screening_evaluation",
  "description": "Analyzes a candidate's resume against a job description and outputs strengths, weaknesses, risk/reward assessment, and an overall fit score.",
  "type": "object",
  "properties": {
    "first_name": {
      "type": "string",
      "description": "Candidate's first name extracted from resume."
    },
    "last_name": {
      "type": "string",
      "description": "Candidate's last name extracted from resume."
    },
    "email": {
      "type": "string",
      "description": "Candidate's email address extracted from resume."
    },
    "candidate_strengths": {
      "type": "array",
      "description": "A list of specific strengths or qualifications that match the job description.",
      "items": {
        "type": "string"
      }
    },
    "candidate_weaknesses": {
      "type": "array",
      "description": "A list of areas where the candidate falls short or lacks alignment with the job requirements.",
      "items": {
        "type": "string"
      }
    },
    "ambiguous_areas": {
      "type": "array",
      "description": "A list of qualifications where resume wording is unclear or generic. HR should verify these during interview.",
      "items": {
        "type": "string"
      }
    },
    "risk_factor": {
      "type": "object",
      "description": "An evaluation of the potential risks of hiring this candidate.",
      "properties": {
        "score": {
          "type": "string",
          "enum": ["Low", "Medium", "High"],
          "description": "The risk level of hiring this candidate."
        },
        "explanation": {
          "type": "string",
          "description": "A brief explanation of the worst-case scenario if the candidate is hired."
        }
      },
      "required": ["score", "explanation"]
    },
    "reward_factor": {
      "type": "object",
      "description": "An evaluation of the potential upside of hiring this candidate.",
      "properties": {
        "score": {
          "type": "string",
          "enum": ["Low", "Medium", "High"],
          "description": "The reward level of hiring this candidate."
        },
        "explanation": {
          "type": "string",
          "description": "A description of the best-case scenario and whether the candidate is a short-term or long-term fit."
        }
      },
      "required": ["score", "explanation"]
    },
    "required_qualifications_met": {
      "type": "string",
      "description": "Format: 'X/8' showing how many required qualifications candidate meets out of total required."
    },
    "preferred_qualifications_met": {
      "type": "string",
      "description": "Format: 'X/8' showing how many preferred qualifications candidate meets out of total preferred."
    },
    "overall_fit_rating": {
      "type": "integer",
      "description": "A rating from 0 to 10 indicating how well the candidate matches the job description.",
      "minimum": 0,
      "maximum": 10
    },
    "justification_for_rating": {
      "type": "string",
      "description": "A summary explaining why the candidate received the specific fit rating, referencing resume and job description alignment."
    }
  },
  "required": [
    "first_name",
    "last_name",
    "email",
    "candidate_strengths",
    "candidate_weaknesses",
    "ambiguous_areas",
    "risk_factor",
    "reward_factor",
    "required_qualifications_met",
    "preferred_qualifications_met",
    "overall_fit_rating",
    "justification_for_rating"
  ]
}

parameters.jsCode:
const items = [];

for (const item of $input.all()) {
  const htmlContent = item.json.output;

  items.push({
    json: {},
    binary: {
      file: await this.helpers.prepareBinaryData(
        Buffer.from(htmlContent, 'utf-8'),
        'index.html',
        'text/html'
      )
    }
  });
}

return items;

const binaryKeys = Object.keys(items[0].binary);
const result = [];

for (const key of binaryKeys) {
  if (key.startsWith('Resume_')) {
    result.push({
      json: { fileName: items[0].binary[key].fileName },
      binary: { file: items[0].binary[key] }
    });
  }
}

return result;

